import {
  axisBottom,
  axisLeft,
  curveCardinal,
  line,
  max,
  scaleLinear,
  select,
  json,
} from "d3";
import { useEffect, useRef, useState } from "react";

export const LineChart = ({ countryName, setCountryName }) => {
  //   const [testdata] = useState([25, 50, 35, 15, 94, 10]);
  //   const [vacdata, setvacData] = useState(
  //     data?.vacData?.data?.map((obj) => {
  //       if (obj?.daily_people_vaccinated === undefined) {
  //         return 0;
  //       }
  //       return obj?.daily_people_vaccinated / 100;
  //     })
  //   );

  const [dailydata, setdailyData] = useState();
  const [date, setDate] = useState();
  const [noMatch, setNoMatch] = useState();
  const svgref = useRef();
  const render = (w, h, svg) => {
    //add this to remove the links
    svg.selectAll("g").remove();
    svg.selectAll("path").remove();
    svg.selectAll("text").remove();
    // title
    svg
      .append("text")
      .attr("x", w / 2 + 100)
      .attr("y", 100)
      .attr("text-anchor", "middle")
      .style("font-family", "Helvetica")
      .style("font-size", 20)
      .text(countryName);
    //xLabel
    svg
      .append("text")
      .attr("x", w / 2)
      .attr("y", h + 30)
      .attr("text-anchor", "middle")
      .style("font-family", "Helvetica")
      .style("font-size", 15)
      .text("Date");

    // yLabel
    svg
      .append("text")
      .attr("text-anchor", "middle")
      .attr("y", -120)
      .attr("transform", "translate(60," + h / 2 + ")rotate(-90)")
      .style("font-family", "Helvetica")
      .style("font-size", 15)
      .text("Cases");
    // setting scalling
    const xScale = scaleLinear()
      .domain([0, dailydata?.length - 1])
      .range([0, w]);

    const yScale = scaleLinear()
      .domain([0, max(dailydata) + max(dailydata) / 10])
      .range([h, 0]);
    const geLine = line()
      .x((d, i) => {
        return xScale(i);
      })
      .y(yScale)
      .curve(curveCardinal);

    // setting the axis
    const xAxis = axisBottom(xScale)
      .ticks(5)
      .tickFormat((i) => {
        return date[i];
      });
    const yAxis = axisLeft(yScale).ticks(5);

    svg.append("g").call(xAxis).attr("transform", `translate(0,${h})`);
    svg.append("g").call(yAxis);

    // setting up the data for the svg
    svg
      .selectAll(".line")
      .data([dailydata])
      .join("path")
      .attr("d", (d) => {
        return geLine(d);
      })
      .attr("fill", "none")
      .attr("stroke", "#d93131");
  };
  useEffect(() => {
    json("vaccinations.json", {}).then((data) => {
      //  console.log(data[0]);
      // setvacData(data[0]);
      const tempData = data?.reduce((accumulator, d) => {
        // get the
        const obj = {
          country: d.country,
          data: d.data,
        };
        // console.log(obj);
        accumulator[d.country] = obj;
        return accumulator;
      }, {});

      const dailydata = tempData[countryName]?.data?.map((obj) => {
        if (obj?.daily_people_vaccinated === undefined) {
          return 0;
        }
        return obj?.daily_people_vaccinated;
      });
      const date = tempData[countryName]?.data?.map((obj) => {
        return obj?.date;
      });
      setDate(date);
      setdailyData(dailydata);
    });
    const w = 600;
    const h = 400;
    const svg = select(svgref.current)
      .attr("width", w)
      .attr("height", h)
      .style("background", "aliceblue")
      .style("margin-top", "100")
      .style("margin-left", "50")
      .style("overflow", "visible");
    if (dailydata && date) {
      // setdailyData(tempData[countryName]);
      render(w, h, svg);
      setNoMatch(false);
    } else {
      setNoMatch(true);
    }
  });

  return (
    <>
      {noMatch ? (
        <p>No matches, please select another country</p>
      ) : (
        <svg ref={svgref}></svg>
      )}
    </>
  );
};
